import React, {Component} from 'react';
import './App.css'
import FilmsCollection from './FilmsCollection'
import _ from 'lodash'
import Modal from '@material-ui/core/Modal';
import { useBottomScrollListener } from 'react-bottom-scroll-listener';

export default class App extends Component{
    constructor(props) {
        super(props)
        this.state = {
            films: [],
            modalOpen: false,
            filmModal: {},
        }
    }

    showFilmModal = (filmId) => {
        const filmToShow = _.find(this.state.films, { id: filmId })
       // alert(filmToShow.title)
        this.setState({
            modalOpen: true,
            filmModal: filmToShow
        })
    }

    componentDidMount() {
        fetch('https://api.themoviedb.org/3/discover/movie?api_key=114ce763417eddc9383aaffd90559895', {})
            .then(res => res.json())
            .then(apiResults => {
                const results = apiResults.results
                this.setState({
                    films: results
                })
            })
    }

    containerRef = useBottomScrollListener(() => {alert('BERZ')});

    handleClose = () => {
        this.setState({ modalOpen: false })
    }

    render() {
        const filmModal = this.state.filmModal

        return (
            <div className="App">
                <h1>Movies of the Moment</h1>

                <FilmsCollection data={this.state.films} showFilmModal={this.showFilmModal}/>

                <Modal
                    open={this.state.modalOpen}
                    onClose={this.handleClose}
                >
                    <div style={{
                        backgroundColor:'white',
                        position: 'absolute',
                        width: 400,
                        top: '20%',
                        left: '20%',
                        }}>
                    <h2>{filmModal.title}</h2>
                    <img
                            src={'https://image.tmdb.org/t/p/w500' + filmModal.poster_path}
                            style={{ height: 250, width: 250 }}
                        />

                        <p>
                        {
                            filmModal.release_date
                        }
                        </p>

                    <p>
                        {
                            filmModal.overview
                        }
                    </p>
                    </div>
                </Modal>
                <div ref={this.containerRef}>Callback will be invoked when this container is scrolled to bottom.</div>

            </div>
        )
    }

}
