import React, { Component } from 'react';
import { withStyles } from '@material-ui/core/styles';
import {Card,
    CardHeader,
    CardMedia,
} from '@material-ui/core'

class FilmsCollection extends Component {
    constructor(props) {
        super(props)
        this.state = {
            data: []
        }
    }

    componentDidUpdate = () => {
        if (this.props.data !== this.state.data) this.setState({data: this.props.data})
    }

    render() {

        return (
            <div style={styles.container}>
                <p>Hiiiiii</p>
                {
                    this.state.data.map(film => <Card style={styles.card} elevation={24}>
                        <CardHeader
                            title={film.title}
                            // subheader={film.category}
                        />
                        <CardMedia
                            image={film.poster}
                            title={film.title}
                            style={styles.media}
                        />
                    </Card>)
                }
            </div>
        );
    }
}

const styles = {
    container: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'flex-start',
    },
    card: {
        width: 300,
        margin: 20,
        flexBasis: '20%'
    },
    media: {
        height: 400,
    },
    actions: {
        display: 'flex',
        justifyContent: 'space-between',
    },
    emotionButtons: {
        display: 'flex',
        alignItems: 'center',
    },
    ratioBar: {
        width: 80,
        margin: 5,
    },
}

const classStyles = () => ({
    colorPrimary: {
        backgroundColor: 'red',
    }
})

export default  withStyles(classStyles)(FilmsCollection);
