import React, {Component} from 'react'
import _ from 'lodash'

// TODO: Amélioration possible! imaginons un grand nombre de pages..

export default class Paging extends Component{
    constructor(props) {
        super(props)
        this.state = {
            selectedPage: 1
        }
    }

    componentDidUpdate = () => {
        if (this.props.selectedPage !== this.state.selectedPage) this.setState({selectedPage: this.props.selectedPage})
    }

    render() {
        return (
            <div style={styles.container}>
                <ul style={styles.ul}>
                    <li
                        style={{...styles.li, ...(this.state.selectedPage === 1 && styles.disabledLi)}}
                        onClick={() => {
                            if (this.state.selectedPage > 1) this.props.changeCurrentPage(this.state.selectedPage - 1)
                        }}
                    >Previous</li>
                    {
                        _.times(this.props.pageCount, i => {
                            return <li
                                style={{...styles.li, ...(i + 1 === this.state.selectedPage && styles.selectedLi)}}
                                onClick={() => {
                                    this.props.changeCurrentPage(i + 1)
                                }}
                            >{i + 1}</li>
                        })
                    }
                    <li
                        style={{...styles.li, ...(this.state.selectedPage === this.props.pageCount && styles.disabledLi)}}
                        onClick={() => {
                            if (this.state.selectedPage < this.props.pageCount) this.props.changeCurrentPage(this.state.selectedPage + 1)
                        }}
                    >Next</li>
                </ul>
            </div>
        )
    }
}

const styles = {
    container: {
        textAlign: 'right',
    },
    ul: {
        display: 'flex',
        paddingLeft: 0,
        listStyle: 'none',
        borderRadius: '.25rem',
        justifyContent: 'flex-end',
    },
    li: {
        position: 'relative',
        display: 'block',
        padding: '.5rem .75rem',
        marginLeft: '-1px',
        lineHeight: '1.25',
        cursor: 'pointer',
        border: 'solid 1px black',
        borderRadius: 5
    },
    selectedLi: {
        backgroundColor: '#2b7af4',
    },
    disabledLi: {
        cursor: 'not-allowed',
        color: 'gray'
    }
}
