import React, {Component} from 'react';
import './App.css'
import FilmsCollection from './FilmsCollection'
import {API_URI} from "./constants";
import _ from 'lodash'

export default class App extends Component{
    constructor(props) {
        super(props)
        this.state = {
            films: [
                {
                    title: 'Titanic',
                    poster: 'http://placekitten.com/g/200/300',
                },
                {
                    title: 'Shaolin Soccer',
                    poster: 'http://placekitten.com/g/200/300',
                },
                {
                    title: 'Taken',
                    poster: 'http://placekitten.com/g/200/300',
                },
                {
                    title: 'Ines Gangbang',
                    poster: 'http://placekitten.com/g/200/300',
                },
            ]
        }
    }

    fetchAll = () => {
        fetch(`${API_URI}/all`, {})
            .then(res => res.json())
            .then(films => this.setState({films}))
    }

    componentDidMount() {
        //this.fetchAll()
    }

    changePerPage = event => {
        this.setState({perPage: parseInt(event.target.value), currentPage: 1,})
    }

    deleteOne = id => {
        fetch(`${API_URI}/${id}`, {method: 'delete'})
            .then(this.fetchAll)
            .catch(error => console.log('ERROR', error))
    }

    render() {
        return (
            <div className="App">
                <p>HELLLOOOOOO</p>
                <FilmsCollection data={this.state.films}/>
            </div>
        )
    }

}
