import React, {Component} from 'react';
import _ from 'lodash'
import {Checkbox, FormControlLabel, FormGroup, FormControl, FormLabel, RadioGroup, Radio} from "@material-ui/core"

export default class Filtering extends Component {
    constructor(props) {
        super(props)
        this.state = {
            firstMount: true, // dirty hack
            perPage: 4,
            checked: [],
        }
    }

    componentDidMount() {
        this.setState({checked: this.props.allGenres})
    }

    componentDidUpdate = () => {
        if (this.props.perPage !== this.state.perPage) this.setState({perPage: this.props.perPage})
        if (this.state.firstMount && this.props.allGenres !== this.state.checked) this.setState({checked: this.props.allGenres, firstMount: false})
    }

    genreChanged = (e) => {

        const { name, checked } = e.target
        const newChecked = checked? _.concat(this.state.checked, name) : _.difference(this.state.checked, [name])

        this.setState(Object.assign({checked: newChecked}))
        this.props.handleGenre(newChecked)


    }

    render() {
        return (
            <div style={styles.container}>
                <FormGroup row style={styles.group}>
                    <FormLabel style={styles.filterLabel}>Filters: </FormLabel>
                    {
                        this.props.allGenres.map(genre =>
                            <FormControlLabel onChange={this.genreChanged} checked={_.includes(this.state.checked, genre)} name={genre} control={<Checkbox/>} label={genre} />
                        )
                    }
                </FormGroup>

                <FormControl component="fieldset" row style={styles.group}>
                    <RadioGroup row
                                aria-label="Number of items per page"
                                name="perPage"
                                value={this.state.perPage}
                                onChange={this.props.changePerPage}
                    >
                        <FormLabel style={styles.filterLabel} component="legend">Items per page: </FormLabel>
                        <FormControlLabel value={4} control={<Radio />} label="4" />
                        <FormControlLabel value={8} control={<Radio />} label="8" />
                        <FormControlLabel value={12} control={<Radio />} label="12" />
                    </RadioGroup>
                </FormControl>

            </div>
        );
    }
}

const styles = {
    container: {
        margin: 30,
        display: 'flex',
        justifyContent: 'space-between',
    },
    filterLabel: {
        marginTop: 'auto',
        marginBottom: 'auto',
        marginRight: 15,
    },
    group: {
        border: 'solid 1px brown',
        borderRadius: 10,
        padding: 3,
        backgroundColor: 'beige',
    }
}
